mapper.insert:

		INSERT INTO farm
            (
            FARM_NO
            ,FARM_NAME
            ,FARM_ADR
            ,FARM_AREA
            ,FARM_CONTENT
            ,USER_ID
            )
    VALUES(
            ( select nvl(max(farm_no),0)+1 from farm )
            ,#{farm_name }
            ,#{farm_adr }
            ,#{farm_area }
            ,#{farm_content }
            ,#{user_id }
            )

mapper.update:
UPDATE farm 
		SET 
			FARM_NAME = #{farm_name}
			,FARM_ADR = #{farm_adr }
			,FARM_AREA = #{farm_area }
			,FARM_CONTENT=#{farm_content }
		WHERE FARM_no=#{farm_no}
