mapper.insert:

		select TO_NUMBER(TO_CHAR(sysdate,'YYYYMMDDHH24MI')||nvl(TO_CHAR(max(substr(order_no,7))),'00'))+1
                    from orders



		INSERT INTO ORDERS
					VALUES (
							#{order_no}
							,sysdate
							,#{order_condition}
							,#{order_invoice}
							,#{order_totalprice}
							,#{order_zip}
							,#{order_adr}
							,#{order_detailadr}
							,#{order_name}
							,#{order_phone}
							,#{order_payment}
							,#{user_id}
							)

mapper.update:

UPDATE ORDERS 
			SET
			order_condition = #{order_condition}
			,order_invoice = #{order_invoice}
WHERE order_no = #{order_no}
